ON n.bus_number = b.bus_number where 1=1

	and b.factory_id = #{factory_id}


	and b.order_id = (SELECT id FROM BMS_ORDER WHERE
	order_no=#{order_no})


	and b.vin = #{bus_vin}


	and b.bus_number = #{bus_number}

