AND t.bus_number IN
#{item}
AND t.tfrom_factory_id = #{tfrom_factory_id}

SELECT t.bus_number,b.vin,o.order_name,c.order_config_name,c.customer,f.factory_name AS factory_name_out, y.factory_name AS factory_name_in,t.tfrom_date,t.tto_date,u.display_name AS name_out,r.display_name AS name_in FROM BMS_PLAN_BUS_TRANSFER_LOG t LEFT OUTER JOIN BMS_PLAN_BUS b ON t.bus_number = b.bus_number LEFT OUTER JOIN BMS_ORDER o ON b.order_id = o.id LEFT OUTER JOIN BMS_ORDER_CONFIG c ON b.order_cofig_id = c.id LEFT OUTER JOIN BMS_BASE_FACTORY f ON t.tfrom_factory_id = f.id LEFT OUTER JOIN BMS_BASE_FACTORY y ON t.tto_factory_id = y.id LEFT OUTER JOIN BMS_BASE_USER u ON t.tfrom_people_id = u.id LEFT OUTER JOIN BMS_BASE_USER r ON t.tto_people_id = r.id WHERE 1=1
AND t.bus_number LIKE CONCAT('%',#{transfer_his_busnumber},'%')
AND b.vin LIKE CONCAT('%',#{transfer_his_vin},'%')
AND o.order_no LIKE CONCAT('%',#{transfer_his_orderno},'%')
AND t.tfrom_factory_id = #{transfer_his_out_factory}
AND t.tfrom_date >= #{transfer_his_out_date_start}
AND t.tfrom_date <= #{transfer_his_out_date_end}
AND t.tto_factory_id = #{transfer_his_in_factory}
AND t.tto_date >= #{transfer_his_in_date_start}
AND t.tto_date <= #{transfer_his_in_date_end}

