AND p.order_id = (SELECT id FROM BMS_ORDER WHERE order_no=#{order_no})
AND p.factory_id = #{factory_id}
AND p.plan_date = #{amend_date}
AND k.key_name LIKE CONCAT('%',#{workshop_name},'%')

