select p.*,sum(qtys) plan_done_qty,o.order_no,o.order_name,o.order_qty,f.factory_name,o.bus_type_id,b.internal_name bus_type,o.order_type from BMS_PLAN_BUS_DISPATCH_PLAN p left join BMS_PLAN_BUS_DISPATCH d on p.id=d.dispatch_plan_id left join BMS_ORDER o on p.order_id=o.id left join BMS_BASE_BUS_TYPE b on o.bus_type_id= b.id left join BMS_BASE_FACTORY f on p.factory_id=f.id where 1=1
and p.factory_id = #{factoryId}
and o.bus_type_id = #{busTypeId}
and (o.order_name like CONCAT('%',#{order},'%') or o.order_no like CONCAT('%',#{order},'%'))
and p.dispatch_date>=#{diapatchDateStart}
and p.dispatch_date <= #{diapatchDateEnd}
and p.status in ('0','1')
group by p.id order by p.status,str_to_date(p.dispatch_date,'%Y-%m-%d') asc
LIMIT #{offset} ,#{pageSize}

select count(id) from ( select p.id from BMS_PLAN_BUS_DISPATCH_PLAN p left join BMS_PLAN_BUS_DISPATCH d on p.id=d.dispatch_plan_id left join BMS_ORDER o on p.order_id=o.id left join BMS_BASE_BUS_TYPE b on o.bus_type_id= b.id left join BMS_BASE_FACTORY f on p.factory_id=f.id where 1=1
and p.factory_id = #{factoryId}
and o.bus_type_id = #{busTypeId}
and (o.order_name like CONCAT('%',#{order},'%') or o.order_no like CONCAT('%',#{order},'%'))
and p.dispatch_date>=#{diapatchDateStart}
and p.dispatch_date <= #{diapatchDateEnd}
and p.status in ('0','1')
group by p.id) tmp

select order_no,CONCAT(order_name,'',bus_type,'',order_qty,'辆') order_desc,order_qty,sum(plan_done_qty) order_done_qty,order_qty-sum(plan_dispatch_qty) order_left,sum(plan_dispatch_qty) order_dispatch from( select p.*,sum(qtys) plan_done_qty,o.order_no,o.order_name,o.order_qty,f.factory_name,o.bus_type_id,b.internal_name bus_type from BMS_PLAN_BUS_DISPATCH_PLAN p left join BMS_PLAN_BUS_DISPATCH d on p.id=d.dispatch_plan_id left join BMS_ORDER o on p.order_id=o.id left join BMS_BASE_BUS_TYPE b on o.bus_type_id= b.id left join BMS_BASE_FACTORY f on p.factory_id=f.id group by p.id ) tmp where 1=1
and (tmp.order_no like CONCAT('%',#{order},'%') or tmp.order_name like CONCAT('%',#{order},'%'))
and tmp.dispatch_date>=#{dispatchStart}
and tmp.dispatch_date<#{dispatchEnd}
group by tmp.order_no
LIMIT #{offset} ,#{pageSize}

select count(id) from ( select p.id from BMS_PLAN_BUS_DISPATCH_PLAN p left join BMS_ORDER o on p.order_id=o.id left join BMS_BASE_BUS_TYPE t on o.bus_type_id=t.id where 1=1
and (o.order_no like CONCAT('%',#{order},'%') or o.order_name like CONCAT('%',#{order},'%'))
and p.dispatch_date>=#{dispatchStart}
and p.dispatch_date<#{dispatchEnd}
group by p.order_id ) tmp

select d.bus_number,IFNULL(b.vin,'') vin,IFNULL(CONCAT(b.left_motor_number,'/',b.right_motor_number),'') motor_number, case d.flag_3c when '0' then '无' else '有' end as flag_3c,IFNULL(d.batch_desc,'') batch_desc,IFNULL(b.customer_number,'') customer_number, IFNULL(d.dispatch_date,'') dispatch_date,IFNULL(d.receiver,'') receiver, u.username dispatcher from BMS_PLAN_BUS_DISPATCH d left join BMS_PLAN_BUS_DISPATCH_PLAN p on d.dispatch_plan_id=p.id left join BMS_PLAN_BUS b on d.bus_number = b.bus_number left join BMS_BASE_USER u on d.dispatcher_id=u.id left join BMS_ORDER o on p.order_id=o.id where 1=1
and o.order_no=#{orderNo}
and (d.bus_number like CONCAT('%',#{busNoVin},'%') or b.vin like CONCAT('%',#{busNoVin},'%'))
and d.dispatch_date>=#{dispatchStart}
and d.dispatch_date<#{dispatchEnd}
LIMIT #{offset} ,#{pageSize}

select count(d.bus_number) from BMS_PLAN_BUS_DISPATCH d left join BMS_PLAN_BUS_DISPATCH_PLAN p on d.dispatch_plan_id=p.id left join BMS_PLAN_BUS b on d.bus_number = b.bus_number left join BMS_BASE_USER u on d.dispatcher_id=u.id left join BMS_ORDER o on p.order_id=o.id where 1=1
and o.order_no=#{orderNo}
and (d.bus_number like CONCAT('%',#{busNoVin},'%') or b.vin like CONCAT('%',#{busNoVin},'%'))
and d.dispatch_date>=#{dispatchStart}
and d.dispatch_date<#{dispatchEnd}

